/**
 * Archetype Definitions
 *
 * 22 Seelen-Archetypen mapped to the Major Arcana.
 * Each archetype includes planet/chakra associations and deep content sections.
 *
 * NOTE: Content fields (wesen, staerken, etc.) contain SHORT PLACEHOLDERS.
 * Full AI-generated content will be added in Task 5.
 */

export interface Archetyp {
  /** Major Arcana number 0-21 */
  id: number;
  /** Matches TarotCard.id from tarot-karten.ts */
  karteId: string;
  /** Unique archetype name e.g. "Die Freie Seele" */
  name: string;
  /** Traditional tarot card name e.g. "Der Narr" */
  karteName: string;
  /** Path to card image */
  image: string;
  /** Planet key matching planeten.ts */
  planet: string;
  /** Chakra key matching chakras.ts */
  chakra: string;
  /** One-sentence identity claim */
  claim: string;
  /** ~200 words about essence (placeholder for now) */
  wesen: string;
  /** ~150 words about strengths (placeholder for now) */
  staerken: string;
  /** ~150 words about shadow side (placeholder for now) */
  schattenSeite: string;
  /** ~150 words about relationships (placeholder for now) */
  inBeziehungen: string;
  /** ~150 words about career (placeholder for now) */
  imBeruf: string;
  /** ~100 words personal message from Elfi (placeholder for now) */
  elfiBotschaft: string;
  /** One power sentence */
  affirmation: string;
}

